import React, { useState } from 'react';

const Admin = () => {
  const [courseName, setCourseName] = useState('');
  const [courseDescription, setCourseDescription] = useState('');
  const [file, setFile] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();
    // submit form data to server
    console.log({ courseName, courseDescription, file });
  };

  return (
    <div>
      <h1>Admin</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="courseName">Course Name</label>
          <input type="text" id="courseName" value={courseName} onChange={(e) => setCourseName(e.target.value)} />
        </div>
        <div>
          <label htmlFor="courseDescription">Course Description</label>
          <textarea id="courseDescription" value={courseDescription} onChange={(e) => setCourseDescription(e.target.value)} />
        </div>
        <div>
          <label htmlFor="file">File Upload</label>
          <input type="file" id="file" onChange={(e) => setFile(e.target.files[0])} />
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default Admin;
