import React from 'react'
import { useState } from 'react';

const AddEdit = () => {
    const [courseName, setCourseName] = useState('');
    const [courseDescription, setCourseDescription] = useState('');
    const [file, setFile] = useState(null);

    const handleSubmit = (e) => {
        e.preventDefault();
        // submit form data to server
        console.log({ courseName, courseDescription, file });
    };

    return (
        <div>
            <h1 className="admin__title">Add New Course</h1>
            <form className="admin__form" onSubmit={handleSubmit}>
                <div className="admin__form-group">
                    <label htmlFor="courseName" className="admin__label">Course Name</label>
                    <input type="text" id="courseName" className="admin__input" value={courseName} onChange={(e) => setCourseName(e.target.value)} />
                </div>
                <div className="admin__form-group">
                    <label htmlFor="courseDescription" className="admin__label">Course Description</label>
                    <textarea id="courseDescription" className="admin__input" value={courseDescription} onChange={(e) => setCourseDescription(e.target.value)} />
                </div>
                <div className="admin__form-group">
                    <label htmlFor="file" className="admin__label">File Upload</label>
                    <input type="file" id="file" className="admin__file-input" onChange={(e) => setFile(e.target.files[0])} />
                </div>
                <button type="submit" className="admin__submit-btn">Submit</button>
            </form>        </div>
    )
}

export default AddEdit
