import React, { useState, useEffect } from "react";
import axios from "axios";
import '../App.css'

const AddEdit = () => {

    const [courseData, setCourseData] = useState([]);
    const [newCourse, setNewCourse] = useState("");
    const [description, setCourseDescription] = useState(1);
    const [url, setURL] = useState(1);

    useEffect(() => {
        fetchCourseData();
    }, []);

    const fetchCourseData = () => {
        axios
            .get("http://localhost:8080/courses/getCourses")
            .then((response) => {
                setCourseData(response.data);
            })
            .catch((error) => {
                console.log(error);
            });
    };

    const addCourse = () => {

        const newCourseData = {
            name: newCourse,
            description: description,
            url: url,
        };
        axios
            .post("http://localhost:8080/courses/addCourse", newCourseData)
            .then((response) => {
                console.log(response.data);
                fetchCourseData();
            })
            .catch((error) => {
                console.log(error);
            });

        alert("Successfully added New course ! ")

    };

    return (
        <div >
            <h2>Admin Page</h2>
            <div>

                <label htmlFor="courseNameInput">course Name:</label>

                <input
                    id="courseNameInput"
                    type="text"
                    placeholder="course Name"
                    value={newCourse}
                    onChange={(event) => setNewCourse(event.target.value)} formNoValidate

                />

                <label htmlFor="descriptionhere">Description:</label>
                <input
                    id="courseDescInput"
                    type="text"
                    placeholder="description"
                    value={description}
                    onChange={(event) => setCourseDescription(event.target.value)} formNoValidate

                />

                <label htmlFor="ratingSelect">Rating:</label>

                <input
                    id="VideoUrl"
                    type="text"
                    placeholder="URL here"
                    value={url}
                    onChange={(event) => setURL(event.target.value)} formNoValidate

                />


                <button className="add_course" onClick={addCourse}>Add course</button>
            </div>

            <div className='t-main'><table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Description</th>
                        <th>Rating</th>
                        <th></th>
                    </tr>
                </thead>

                <tbody>
                    {courseData.map((course) => (
                        <tr key={course.id}>
                            <td>{course.name}</td>
                            <td>{course.description}</td>
                            <td>{course.url}</td>
                            <td>
                                <button className='del_btn'>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table></div>
        </div>
    );
};
export default AddEdit;
